import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import 'antd/dist/antd.css';
import { Layout, Form, Input, Button } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';

function App() {
  const { Header, Content, Footer } = Layout;
  const [form] = Form.useForm();
  const [, forceUpdate] = useState();

  useEffect(() => {
    forceUpdate({});
  }, []);

  return (<div className="App">
    <Router>
      <Layout className="layout">
        <Header >
          <div className="container d-flex" style={{ alignItems: 'center', position: 'relative' }}>
            <div className="logo">
              <img src={"logo.png"} alt="Mohi.vn" title="Mohi.vn" />
            </div>
            <Form form={form} name="horizontal_login" layout="inline" style={{ position: 'absolute', right: 0 }}>
              <Form.Item name="username">
                <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Tài khoản" />
              </Form.Item>
              <Form.Item name="password">
                <Input
                  prefix={<LockOutlined className="site-form-item-icon" />}
                  type="password"
                  placeholder="Mật khẩu"
                />
              </Form.Item>
              <Form.Item >
                <Button type="primary">
                  Log in
                  </Button>
              </Form.Item>
            </Form>
          </div>
        </Header>
        <Content style={{ padding: '0 50px' }}>
          <div className="site-layout-content">
            <Route exact path="/" render={() => <div>Home</div>} />
            <Route exact path="/nav1" render={() => <div>nav1</div>} />
            <Route exact path="/nav2" render={() => <div>nav2</div>} />
            <Route exact path="/nav3" render={() => <div>nav3</div>} />
          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>Ant Design ©2018 Created by Ant UED</Footer>
      </Layout>
    </Router>
  </div>);
}

export default App;
