import React from 'react';
import { Comment, Avatar, Form, Input } from 'antd';

function CreatePost() {
    const Textarea = { Input };
    const Create = ({ onChange, onSubmit, value }) => (
        <>
            <Form onSubmit={onSubmit}>
                <Form.Item>
                    <Textarea onChange={onChange} placeholder="Viết bình luận..." value={value}></Textarea>
                </Form.Item>
            </Form>
        </>
    );

    return (
        <>
            <Comment
                avatar={
                    <Avatar
                        src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                        alt="Han Solo"
                    />
                }
                content={
                    <Create
                    />
                }
            />
        </>
    )
}

export default CreatePost;
