import React, { useState, useRef } from 'react';
import './CreatePost.css';
import { Comment, Avatar, Form, Input, Upload, Modal, List } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import { FcAddImage } from "react-icons/fc";

function CreatePost() {
    const [previewVisible, setPreviewVisible] = useState(false);
    const [previewImage, setPreviewImage] = useState();
    const [previewTitle, setPreviewTitle] = useState();
    const [fileList, setFileList] = useState([]);
    const uploadImageNewPost = useRef();

    function getBase64(file) {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => resolve(reader.result);
            reader.onerror = error => reject(error);
        });
    }
    const handleUploadImage = () => {
        uploadImageNewPost.current.upload.uploader.onClick()
    }
    const handleCancel = () => setPreviewVisible(false);
    const handleChange = ({ fileList }) => setFileList(fileList);
    const handlePreview = async file => {
        if (!file.url && !file.preview) {
            file.preview = await getBase64(file.originFileObj);
        }
        setPreviewImage(file.url || file.preview)
        setPreviewVisible(true)
        setPreviewTitle(file.name || file.url.substring(file.url.lastIndexOf('/') + 1))
    };

    const uploadButton = (
        <div>
            <PlusOutlined />
            <div className="ant-upload-text" >Upload</div>
        </div>
    );

    const Editor = ({ onChange, onSubmit, value }) => (
        <>
            <Form onSubmit={onSubmit}>
                <Form.Item>
                    <Input
                        style={{ wordWrap: "break-word", whiteSpace: "pre-wrap", outline: 'none', userSelect: 'text', overflowWrap: 'break-word' }}
                        className="input-create-post"
                        onChange={onChange}
                        placeholder="Bạn đang nghĩ dì?" value={value}
                    />

                </Form.Item>
            </Form>
        </>
    );

    return (
        <>
            <div className="create-post p-3">
                <Comment
                    avatar={
                        <Avatar
                            src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                            alt="Han Solo"
                        />
                    }
                    content={
                        <>
                            <Editor
                            />
                            <div className="d-flex">
                                <Upload
                                    ref={uploadImageNewPost}
                                    multiple
                                    action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                                    listType="picture-card"
                                    fileList={fileList}
                                    onPreview={handlePreview}
                                    onChange={handleChange}
                                >
                                    {uploadButton}
                                </Upload>
                                <Modal
                                    visible={previewVisible}
                                    title={previewTitle}
                                    footer={null}
                                    onCancel={handleCancel}
                                >
                                    <img alt="example" style={{ width: '100%' }} src={previewImage} />
                                </Modal>
                            </div>
                        </>
                    }
                />
                <hr />
                <div
                    className="d-flex action-new-post"
                    style={{ justifyContent: "space-around", fontSize: "14px" }}
                >
                    <div
                        onClick={handleUploadImage}
                    >
                        <FcAddImage />Ảnh/Video
                    </div>
                    <div>
                        <FaUserTag />Gắn thẻ bạn bè
                    </div>
                </div>
            </div>
        </>
    )
}

export default CreatePost;
