import React, { createElement, useState } from 'react';
import { Layout, Card, Avatar, Tooltip } from 'antd';
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';
import { UserOutlined, AntDesignOutlined } from '@ant-design/icons';
import { AiOutlineLike } from "react-icons/ai";
import { FaRegCommentAlt } from "react-icons/fa";
import { RiShareForwardLine } from "react-icons/ri";
import './NewFeed.css';

import { Comment } from 'antd';
import moment from 'moment';
import { DislikeOutlined, LikeOutlined, DislikeFilled, LikeFilled } from '@ant-design/icons';

function NewFeed() {
    const { Content } = Layout;
    const { Meta } = Card;
    const [likes, setLikes] = useState(0);
    const [dislikes, setDislikes] = useState(0);
    const [action, setAction] = useState(null);

    const like = () => {
        setLikes(1);
        setDislikes(0);
        setAction('liked');
    };

    const dislike = () => {
        setLikes(0);
        setDislikes(1);
        setAction('disliked');
    };

    const actions = [
        <Tooltip key="comment-basic-like" title="Like">
            <span onClick={like}>
                {createElement(action === 'liked' ? LikeFilled : LikeOutlined)}
                <span className="comment-action">{likes}</span>
            </span>
        </Tooltip>,
        <Tooltip key="comment-basic-dislike" title="Dislike">
            <span onClick={dislike}>
                {React.createElement(action === 'disliked' ? DislikeFilled : DislikeOutlined)}
                <span className="comment-action">{dislikes}</span>
            </span>
        </Tooltip>,
        <span key="comment-basic-reply-to">Reply to</span>,
    ];
    return (
        <Content className="pl-3 pr-3">
            <Card
                title={<>
                    <Avatar src="images/gaixinh.jpg" />
                    <span style={{ paddingLeft: "10px" }}>
                        Nguyễn Văn Phước
                    </span>
                </>}
                style={{ width: '100%', marginBottom: "10px" }}
                cover={
                    <>
                        <div className="content-post" style={{ maxWidth: '100%' }}>
                            Project will complete on four month - make by Mohi Team
                    </div>
                        <img
                            alt="example"
                            src="https://anhnendep.net/wp-content/uploads/2016/04/hinh-gai-dep-hot-gril-viet-nam-14.jpg"
                        />
                    </>
                }
                actions={[
                    <AiOutlineLike key="like" />,
                    <FaRegCommentAlt key="comment" />,
                    <RiShareForwardLine key="share" />,
                ]}
            >
                <Avatar.Group
                    maxCount={2}
                    maxStyle={{
                        color: '#f56a00',
                        backgroundColor: '#fde3cf',
                    }}
                >
                    <Avatar src="images/gaixinh.jpg" />
                    <Avatar
                        style={{
                            backgroundColor: '#f56a00',
                        }}
                    >
                        K
                </Avatar>
                    <Tooltip title="Ant User" placement="top">
                        <Avatar
                            style={{
                                backgroundColor: '#87d068',
                            }}
                            icon={<UserOutlined />}
                        />
                    </Tooltip>
                    <Avatar
                        style={{
                            backgroundColor: '#1890ff',
                        }}
                        icon={<AntDesignOutlined />}
                    />
                </Avatar.Group>
                <span style={{ marginLeft: "5px" }}>Nguyễn Văn Phước, Trần Tiến Đạt và 1.000 người khác</span>

            </Card>

            <Comment
                actions={actions}
                author={<a>Han Solo</a>}
                avatar={
                    <Avatar
                        src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                        alt="Han Solo"
                    />
                }
                content={
                    <p>
                        We supply a series of design principles, practical patterns and high quality design
                        resources (Sketch and Axure), to help people create their product prototypes beautifully
                        and efficiently.
                            </p>
                }
                datetime={
                    <Tooltip title={moment().format('YYYY-MM-DD HH:mm:ss')}>
                        <span>{moment().fromNow()}</span>
                    </Tooltip>
                }
            />
            <Card
                title={<>
                    <Avatar src="images/gaixinh.jpg" />
                    <span style={{ paddingLeft: "10px" }}>
                        Nguyễn Văn Phước
                    </span>
                </>}
                style={{ width: '100%' }}
                cover={
                    <>
                        <div className="content-post" style={{ maxWidth: '100%' }}>
                            Mình ít đăng bài nhưng vẫn hỗ trợ đầy đủ nha mọi người.Seeding PRO by Phước Dz 🤣🤣🤣
                    </div>
                        <img
                            alt="example"
                            src="images/gaixinh.jpg"
                        />
                    </>
                }
                actions={[
                    <SettingOutlined key="setting" />,
                    <EditOutlined key="edit" />,
                    <EllipsisOutlined key="ellipsis" />,
                ]}
            >

            </Card>
        </Content>
    )
}

export default NewFeed;
