import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';
import {Button} from 'react-bootstrap';

function App() {

  const [concho, setConCho] = useState('màu đen') // khai báo state con chó màu đen,ukm concho nó sẽ mang chữ màu đen còn setConCho đợi tí

  useEffect(() => {
    // hàm này chạy như componentDidmount ấy nhớ k, nhớ
    alert('a')
  },[concho])

  function handleChangeColorDog() {
    setConCho('màu cam')
  }

  return (
    <div className="App">
      <div>{concho}</div>
      <div onClick={() => handleChangeColorDog()}>đổi màu</div> {/* nó chạy lại đấy để t nghĩ xem sao nó lại chạy lại 2 lần, để xem mấy video đã :v*/}
      <Button>Test</Button>
    </div>
  );
}

export default App;
