import React, { createElement, useState, useRef } from 'react';
import './PostNewFeed.css';
import {
    UserOutlined,
    AntDesignOutlined,
    DislikeOutlined,
    LikeOutlined,
    DislikeFilled,
    LikeFilled
} from '@ant-design/icons';
import {
    Form,
    Input,
    Comment,
    List,
    Card,
    Avatar,
    Tooltip
} from 'antd';
import { AiOutlineLike, AiFillLike } from "react-icons/ai";
import { FaRegCommentAlt } from "react-icons/fa";
import { RiShareForwardLine } from "react-icons/ri";
import moment from 'moment';

function PostNewFeed() {

    const [likes, setLikes] = useState(0);
    const [dislikes, setDislikes] = useState(0);
    const [action, setAction] = useState(null);
    const focusComment = useRef();
    const like = () => {
        setLikes(1);
        setDislikes(0);
        setAction('liked');
    };
    const dislike = () => {
        setLikes(0);
        setDislikes(1);
        setAction('disliked');
    };

    function handleFocusComment() {
        focusComment.current.focus();
    };

    const actions = [
        <Tooltip key="comment-basic-like" title="Like">
            <span onClick={like}>
                {createElement(action === 'liked' ? LikeFilled : LikeOutlined)}
                <span className="comment-action">{likes}</span>
            </span>
        </Tooltip>,
        <Tooltip key="comment-basic-dislike" title="Dislike">
            <span onClick={dislike}>
                {React.createElement(action === 'disliked' ? DislikeFilled : DislikeOutlined)}
                <span className="comment-action">{dislikes}</span>
            </span>
        </Tooltip>,
        <span key="comment-basic-reply-to">Trả lời</span>,
    ];

    const Editor = ({ onChange, onSubmit, submitting, value }) => (
        <>
            <Form onSubmit={onSubmit}>
                <Form.Item>
                    <Input ref={focusComment} className="input-comment-newfeed" onChange={onChange} placeholder="Viết bình luận..." value={value} />
                </Form.Item>
            </Form>
        </>
    );
    return (
        <>
            <List
                className="ant-card-bordered"
                style={{ width: '100%', marginBottom: "10px", background: 'white' }}
            >
                <Card
                    title={<>
                        <Avatar src="images/gaixinh.jpg" />
                        <span style={{ paddingLeft: "10px" }}>
                            Nguyễn Văn Phước
                    </span>
                    </>}
                    style={{ width: '100%', marginBottom: "10px" }}
                    cover={
                        <>
                            <div className="content-post" style={{ maxWidth: '100%' }}>
                                Project will complete on four month - make by Mohi Team
                    </div>
                            <img
                                alt="example"
                                src="https://anhnendep.net/wp-content/uploads/2016/04/hinh-gai-dep-hot-gril-viet-nam-14.jpg"
                            />
                        </>
                    }
                    actions={[
                        <a >< AiOutlineLike className="action-post" key="like" /></a>,
                        <a onClick={() => handleFocusComment()}>< FaRegCommentAlt className="action-post" key="comment" /></a>,
                        <a><RiShareForwardLine className="action-post" key="share" /></a>,
                    ]}
                >
                    <Avatar.Group
                        maxCount={2}
                        maxStyle={{
                            color: '#f56a00',
                            backgroundColor: '#fde3cf',
                        }}
                    >
                        <Avatar src="images/gaixinh.jpg" />
                        <Avatar
                            style={{
                                backgroundColor: '#f56a00',
                            }}
                        >
                            K
                </Avatar>
                        <Tooltip title="Ant User" placement="top">
                            <Avatar
                                style={{
                                    backgroundColor: '#87d068',
                                }}
                                icon={<UserOutlined />}
                            />
                        </Tooltip>
                        <Avatar
                            style={{
                                backgroundColor: '#1890ff',
                            }}
                            icon={<AntDesignOutlined />}
                        />
                    </Avatar.Group>
                    <span style={{ marginLeft: "5px" }}>Nguyễn Văn Phước, Trần Tiến Đạt và 1.000 người khác</span>

                </Card>
                <Comment
                    style={{ boxSizing: 'border-box', border: '1px solid #f0f0f0' }}
                    avatar={
                        <Avatar
                            src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                            alt="Han Solo"
                        />
                    }
                    content={
                        <Editor
                        />
                    }
                />
                <Comment
                    actions={actions}
                    author={<a>Han Solo</a>}
                    avatar={
                        <Avatar
                            src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                            alt="Han Solo"
                        />
                    }
                    content={
                        <p>
                            We supply a series of design principles, practical patterns and high quality design
                            resources (Sketch and Axure), to help people create their product prototypes beautifully
                            and efficiently.
                            </p>
                    }
                    datetime={
                        <Tooltip title={moment().format('YYYY-MM-DD HH:mm:ss')}>
                            <span>{moment().fromNow()}</span>
                        </Tooltip>
                    }
                />
            </List>
        </>
    )
}

export default PostNewFeed;
