import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';
import {Button} from 'react-bootstrap';

function App() {

  const [concho, setConCho] = useState('màu đen') // khai báo state con chó màu đen,ukm concho nó sẽ mang chữ màu đen còn setConCho đợi tí

  useEffect((pageCurrent) => {
    setConCho('màu trắng') // đây là set lại concho mang chữ thành màu trắng
    // hàm này chạy như componentDidmount ấy nhớ k, nhớ
  },[pageCurrent]) // như vầy thì nó sẽ chạy lại khi cái pageCurrent thay đổi ok
  return (
    <div className="App">
      <div>{concho}</div>
      <Button>Test</Button> // chỉ có 3 dòng là thành Hook nha :v
    </div>
  );
}

export default App;
